mapper.select:
select bank_id userId
from test_customer
where bank_id = #{userId}

select bank_id userid
from test_customer
where name = #{name} and rrn = #{rrn}

select email
from test_customer
where email = #{email}

select phone_num phoneNum
from test_customer
where phone_num = #{phoneNum}

mapper.update:
